mapper.select:
select * from (select rownum rnum, a.* from
(select jspuser.* from jspuser order by userId) a)
where rnum between #{page}*#{pageSize}-(#{pageSize}-1) and #{page}*#{pageSize}

mapper.update:
UPDATE	JSPUSER
SET		NAME	= #{name}
	,	PASS	= #{pass}
	,	ADDR1	= #{addr1}
	,	ADDR2	= #{addr2}
	,	ZIPCD	= #{zipcd}
	,	BIRTH	= #{birth}
	,	EMAIL	= #{email}
	,	TEL		= #{tel}
	,	PROFILE = #{profile}
WHERE	USERID	= #{userId}

mapper.select:
SELECT	PROFILE
FROM	JSPUSER
WHERE	USERID = #{userId}
